INSERT INTO movie (title, description, release_date, raterCount, rating, poster_url, price, duration)
    VALUES (N'Man of Steel', N'A young boy learns that he has extraordinary powers and is not of this earth. As a young man, he journeys to discover where he came from and what he was sent here to do. But the hero in him must emerge if he is to save the world from annihilation and become the symbol of hope for all mankind.', '2013-06-12', 15750, 3.3, 'https://image.tmdb.org/t/p/w500/8GFtkImmK0K1VaUChR0n9O61CFU.jpg', 4.95, 143);
INSERT INTO movie (title, description, release_date, raterCount, rating, poster_url, price, duration)
    VALUES (N'Evangelion: 3.0+1.0 Thrice Upon a Time', N'In the aftermath of the Fourth Impact, stranded without their Evangelions, Shinji, Asuka and Rei find refuge in one of the rare pockets of humanity that still exist on the ruined planet Earth. There, each lives a life far different from their days as an Evangelion pilot. However, the danger to the world is far from over. A new impact is looming on the horizon—one that will prove to be the true end of Evangelion.', '2021-03-08', 881, 4.1, 'https://image.tmdb.org/t/p/w500/md5wZRRj8biHrGtyitgBZo7674t.jpg', 6.15, 155);
INSERT INTO movie (title, description, release_date, raterCount, rating, poster_url, price, duration)
    VALUES (N'How to Train Your Dragon: The Hidden World', N'As Hiccup fulfills his dream of creating a peaceful dragon utopia, Toothless’ discovery of an untamed, elusive mate draws the Night Fury away. When danger mounts at home and Hiccup’s reign as village chief is tested, both dragon and rider must make impossible decisions to save their kind.', '2019-01-03', 6847, 3.9, 'https://image.tmdb.org/t/p/w500/xvx4Yhf0DVH8G4LzNISpMfFBDy2.jpg', 5.85, 104);
INSERT INTO movie (title, description, release_date, raterCount, rating, poster_url, price, duration)
    VALUES (N'Mission: Impossible - The Final Reckoning', N'Ethan Hunt and team continue their search for the terrifying AI known as the Entity — which has infiltrated intelligence networks all over the globe — with the world''s governments and a mysterious ghost from Hunt''s past on their trail. Joined by new allies and armed with the means to shut the Entity down for good, Hunt is in a race against time to prevent the world as we know it from changing forever.', '2025-05-17', 2043, 3.6, 'https://image.tmdb.org/t/p/w500/z53D72EAOxGRqdr7KXXWp9dJiDe.jpg', 5.4, 170);
INSERT INTO movie (title, description, release_date, raterCount, rating, poster_url, price, duration)
    VALUES (N'Kelly + Victor', N'When Kelly meets Victor on the dance floor of a Liverpool nightclub, the attraction is instant. After wandering through the night they find themselves at her flat, making love with a passion and urgency that neither had experienced before. Both Kelly and Victor are struggling to get by as best they can, while the people around them are choosing illegal lifestyles; she is escaping a brutish former lover, while he is being dragged into a world of drugs. It''s when they make love that their darker instincts take over.', '2012-10-16', 32, 2.7, 'https://image.tmdb.org/t/p/w500/qLi6TrmPgubYlc9OBek9wWMPfDr.jpg', 4.05, 95);       
INSERT INTO movie (title, description, release_date, raterCount, rating, poster_url, price, duration)
    VALUES (N'Jujutsu Kaisen 0', N'Yuta Okkotsu is a nervous high school student who is suffering from a serious problem—his childhood friend Rika has turned into a curse and won''t leave him alone. Since Rika is no ordinary curse, his plight is noticed by Satoru Gojo, a teacher at Jujutsu High, a school where fledgling exorcists learn how to combat curses. Gojo convinces Yuta to enroll, but can he learn enough in time to confront the curse that haunts him?', '2021-12-24', 1571, 4.1, 'https://image.tmdb.org/t/p/w500/23oJaeBh0FDk2mQ2P240PU9Xxfh.jpg', 6.15, 105);
INSERT INTO movie (title, description, release_date, raterCount, rating, poster_url, price, duration)
    VALUES (N'Superman', N'Superman, a journalist in Metropolis, embarks on a journey to reconcile his Kryptonian heritage with his human upbringing as Clark Kent.', '2025-07-09', 3736, 3.7, 'https://image.tmdb.org/t/p/w500/wPLysNDLffQLOVebZQCbXJEv6E6.jpg', 5.55, 130);
INSERT INTO movie (title, description, release_date, raterCount, rating, poster_url, price, duration)
    VALUES (N'Spectre', N'A cryptic message from Bond’s past sends him on a trail to uncover a sinister organization. While M battles political forces to keep the secret service alive, Bond peels back the layers of deceit to reveal the terrible truth behind SPECTRE.', '2015-10-26', 10897, 3.3, 'https://image.tmdb.org/t/p/w500/zj8ongFhtWNsVlfjOGo8pSr7PQg.jpg', 4.95, 148);

INSERT INTO movie (title, description, release_date, raterCount, rating, poster_url, price, duration)
    VALUES (N'Crouching Tiger, Hidden Dragon', N'Two warriors in pursuit of a stolen sword and a notorious fugitive are led to an impetuous, physically-skilled, teenage nobleman''s daughter, who is at a crossroads in her life.', '2000-07-06', 3407, 3.7, 'https://image.tmdb.org/t/p/w500/iNDVBFNz4XyYzM9Lwip6atSTFqf.jpg', 5.55, 120);
INSERT INTO movie (title, description, release_date, raterCount, rating, poster_url, price, duration)
    VALUES (N'Saw II', N'The chilling and relentless Jigsaw killer returns to terrorize the city once again. When a gruesome murder victim emerges with unmistakable traces of Jigsaw''s sinister methods, Detective Eric Matthews is thrust into a high-stakes investigation. To his surprise, apprehending Jigsaw seems almost too easy, but what he doesn''t realize is that being caught is merely another piece of Jigsaw''s intricate puzzle.', '2005-10-28', 5659, 3.3, 'https://image.tmdb.org/t/p/w500/gTnaTysN8HsvVQqTRUh8m35mmUA.jpg', 4.95, 93);
INSERT INTO movie (title, description, release_date, raterCount, rating, poster_url, price, duration)
    VALUES (N'Hidden Face', N'Su-yeon, a cellist in an orchestra led by her fiancé and conductor Sung-jin, disappears one day, leaving behind only a video recording. Sung-jin is devastated over the loss of Su-yeon, but feels a strong attraction to Mi-ju, a cellist who fills in for his fiancée. Then one rainy night, Sung-jin and Mi-ju get swept away by their mutual desires for each other and commit an unforgivable act at Su-yeon’s house. Meanwhile, Su-yeon, who is thought to have gone missing, remains trapped in a secret room inside her house, and watches the naked truth unravel before her.', '2024-11-20', 46, 2.3, 'https://image.tmdb.org/t/p/w500/slRaAeEWbU8gM834X10qNhBhVIv.jpg', 3.45, 113);
INSERT INTO movie (title, description, release_date, raterCount, rating, poster_url, price, duration)
    VALUES (N'Dumb and Dumber', N'Lloyd and Harry are two men whose stupidity is really indescribable. When Mary, a beautiful woman, loses an important suitcase with money before she leaves for Aspen, the two friends (who have found the suitcase) decide to return it to her. After some "adventures" they finally get to Aspen where, using the lost money they live it up and fight for Mary''s heart.', '1994-12-16', 6432, 3.3, 'https://image.tmdb.org/t/p/w500/4LdpBXiCyGKkR8FGHgjKlphrfUc.jpg', 4.95, 107);
INSERT INTO movie (title, description, release_date, raterCount, rating, poster_url, price, duration)
    VALUES (N'Chernobyl: Abyss', N'The aftermath of a shocking explosion at the Chernobyl nuclear power station made hundreds of people sacrifice their lives to clean up the site of the catastrophe and to successfully prevent an even bigger disaster that could have turned a large part of the European continent into an uninhabitable exclusion zone. This is their story.', '2021-04-15', 548, 3.1, 'https://image.tmdb.org/t/p/w500/AmJLuHjxPdIJO6vmymeWADG6jK5.jpg', 4.65, 136);
INSERT INTO movie (title, description, release_date, raterCount, rating, poster_url, price, duration)
    VALUES (N'Evangelion: 2.0 You Can (Not) Advance', N'Under constant attack by monstrous creatures called Angels that seek to eradicate humankind, U.N. Special Agency NERV introduces two new EVA pilots to help defend the city of Tokyo-3: the mysterious Makinami Mari Illustrous and the intense Asuka Langley Shikinami. Meanwhile, Gendo Ikari and SEELE proceed with a secret project that involves both Rei and Shinji.', '2009-06-26', 940, 3.9, 'https://image.tmdb.org/t/p/w500/7VLYN2CfJpB6PrcuzDKKqdGSUi6.jpg', 5.85, 112);
INSERT INTO movie (title, description, release_date, raterCount, rating, poster_url, price, duration)
    VALUES (N'Cold Fish', N'Shamoto runs a small tropical fish shop. When his daughter Mitsuko is caught shoplifting at a grocery store a man named Murata steps in to settle things between the girl and the store manager. Murata also runs a tropical fish shop and he and Shamoto soon become friendly. However Murata hides many dark secrets behind his friendly face.', '2011-01-29', 372, 3.5, 'https://image.tmdb.org/t/p/w500/669GmEZsyMlPliURX5myHTStslv.jpg', 5.25, 146);
INSERT INTO movie (title, description, release_date, raterCount, rating, poster_url, price, duration)
    VALUES (N'Dr. No', N'Agent 007 battles mysterious Dr. No, a scientific genius bent on destroying the U.S. space program. As the countdown to disaster begins, Bond must go to Jamaica, where he encounters beautiful Honey Ryder, to confront a megalomaniacal villain in his massive island headquarters.', '1962-10-07', 3856, 3.5, 'https://image.tmdb.org/t/p/w500/9zCOLJmLNst0sCPZlkW1IRoH65E.jpg', 5.25, 110);
