UPDATE [dbo].[people]
    SET biography = N'Shinullichirō Miki is a Japanese voice actor affiliated with 81 Produce and most known for his role as Kojiro (James) in Pokémon. Miki is also a singer with the four-man band Weiß. Miki is also very active in BL dramas. He won Best Actors in supporting roles in the 4th Seiyu Awards. He often gets cast as handsome, young men in anime roles.', dob = '1968-03-18', photo_url = 'https://image.tmdb.org/t/p/w500/yL9Tx2HZgK7t5AssYl6oGE9bjdj.jpg'
